mapper.update:
update emp_ent  set update_time = #{update_time},operate_by = #{operate_by}
,group_id = #{group_id}
,enterprise = #{enterprise}
,addr = #{addr}
,director = #{director}
,phone = #{phone}
,simple_name = #{simple_name}
,enable = #{enable}
,deleted = #{deleted}
where id = #{id}

mapper.select:
select A.id, A.group_id, A.enterprise, A.addr, A.director, A.phone, A.simple_name, A.create_time, A.operate_by, A.update_time, A.enable, A.deleted,B.name as group_name from emp_ent A
LEFT JOIN emp_group B ON A.group_id = B.id where A.deleted = 0
 and A.group_id = #{group_id}
